sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
		this.legalDefaultDays=2;
		this.facilitiesDefaultDays=3;
		this.hrDefaultDays=5;
    },
	adddefaultDays : function(whtNeeded,neededDate){
		var rightNow = new GlideDateTime();
		var givenDate = new GlideDateTime(neededDate);	
		gs.info(GlideDate.subtract(rightNow,givenDate).getRoundedDayPart());
		var daysbtw = GlideDate.subtract(rightNow,givenDate).getRoundedDayPart();
		var defaultDays = 0;

		if(whtNeeded=='hr' && daysbtw < this.hrDefaultDays)
			defaultDays= this.hrDefaultDays;
		else if(whtNeeded=='facilities' && daysbtw < this.facilitiesDefaultDays)
			defaultDays= this.facilitiesDefaultDays;
		else if(whtNeeded=='legal' && daysbtw < this.legalDefaultDays)
			defaultDays= this.legalDefaultDays;
		gs.info(defaultDays);
		if(defaultDays==0){
			return neededDate;
		}else{
			rightNow.addDaysLocalTime(defaultDays);
			return rightNow;
		}	
    },
    type: 'NeedItUtils'
};
